import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.scheduling.annotation.Scheduled;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

@Service
public class UpdateTradePoolStateTaskService {

    @Autowired
    private SuiTradePoolService suiTradePoolService;

    @Autowired
    private TradePoolEventRepository tradePoolEventRepository;

    @Autowired
    private TradePoolEventService tradePoolEventService;

    @Scheduled(fixedDelayString = "${sui.contract.update-trade-pool-states.fixed-delay:5000}")
    @Transactional
    public void updateTradePoolStates() {
        tradePoolEventRepository.findByStatusIsNull().forEach(e -> {
            String objectId = e.getId();
            if (TradePoolEventService.isDeletionCommand(e)) {
                suiTradePoolService.deleteTradePool(objectId);
            } else {
                suiTradePoolService.updateTradePoolState(objectId);
            }
            tradePoolEventService.updateStatusToProcessed(e);
        });
    }
}
